    private void ButtonEliminarCRUDMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_ButtonEliminarCRUDMouseClicked
        int temp;
        try {
            temp = Integer.parseInt(Fieldindiceelminar.getText());
            listapc.remove(temp);
        } catch (Exception e) {
        }
        PanelEliminar.setVisible(false);
        PanelCRUD.setVisible(true);
        JFrameEliminar.setVisible(false);
        JFrameCrud.setVisible(true);
        JFrameCrud.setSize(510, 520);
        Fieldindiceelminar.setText("");
        
    }//GEN-LAST:event_ButtonEliminarCRUDMouseClicked

    private void ButtonIngresarPcMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_ButtonIngresarPcMouseClicked
        Scanner leer = new Scanner(System.in);
        boolean b = false;
        PanelBase.setVisible(false);
        System.out.println("PC a ingresar a la consola");
        for (Pc pc : listapc) {
            System.out.println(""+listapc.indexOf(pc)+"- "+ pc);
        }
        System.out.println("Ingrese el indice de su PC: ");
        int indice = leer.nextInt();
        do{
            if(listapc.size() > indice && indice >= 0){
                b = true;
            }else{
                b = false;
                System.out.println("Esa PC no existe");
                System.out.println("Ingrese el indice de su PC: ");
                indice = leer.nextInt();
            }
        
        }while(b != true);
        do{
            int verifier=0;
            b= false;
            System.out.print(listapc.get(indice).getHostname() + "#");
            String comando = leer.next();
            
            System.out.println("");
            System.out.println("Pinging to "+listapc.get(indice).getDireccionIp()+" with 32 bits of data: ");
            
            if(comando.equals("show")){
                System.out.println("Ip: "+ listapc.get(indice).getDireccionIp()+"\nMask: "+ listapc.get(indice).getMascaraRed());
                b = false;
            }else if(comando.equals("exit")){
                System.out.println("Saliendo de la consola, devolviendo al GUI...");
                b = true;
            }else{
                String nuevoip="";
                for (int i = 0; i < comando.length(); i++) {
                    if(i >= 5){
                        nuevoip+= comando.charAt(i);
                    }
                }
                for (Pc p : listapc) {
                    if(p.getDireccionIp().equals(nuevoip)){
                        //System.out.println("ping exitoso");
                        verifier++;
                        
                        String bi="";
                        bi = listapc.get(indice).getDireccionIp().split("\\.")[3];
                        // binario ip
                        int ipbinariotemp =Integer.parseInt(bi);
                        int modulo;
                        String ipbinario="";
                        while(ipbinariotemp>0){
                            modulo = ipbinariotemp%2;
                            ipbinario = modulo+ ipbinario;
                            ipbinariotemp = ipbinariotemp/2;
                        }
                        String bm ="";
                        bm = listapc.get(indice).getMascaraRed().split("\\.")[3];
                        //binario mask
                        int maskbinariotemp = Integer.parseInt(bm);
                        int modulo2;
                        String maskbinario="";
                        while(maskbinariotemp>0){
                            modulo2 = maskbinariotemp%2;
                            maskbinario = modulo2+maskbinario;
                            maskbinariotemp= maskbinariotemp/2;
                        }
                        String bi2="";
                        bi2 = p.getDireccionIp().split("\\.")[3];
                        //binario ip 2
                        int ipbinariotemp2 = Integer.parseInt(bi2);
                        int modulo3;
                        String ipbinario2="";
                        while(ipbinariotemp2>0){
                            modulo3 = ipbinariotemp2%2;
                            ipbinario2 = modulo3+ ipbinario2;
                            ipbinariotemp2 = ipbinariotemp2/2;
                        }
                        //comprobar
                        int contaunos=0;
                        String ip1="";
                        String ip2="";
                        for (int i = 0; i < maskbinario.length(); i++) {
                            if(maskbinario.charAt(i)== '1'){
                                contaunos++;
                            }else{
                                break;
                            }
                        }
                        for (int i = 0; i < contaunos; i++) {
                            ip1 += ipbinario.charAt(i);
                        }
                        for (int i = 0; i < contaunos; i++) {
                            ip2 += ipbinario2.charAt(i);
                        }
//                        System.out.println(ipbinario);
//                        System.out.println(ipbinario2);
//                        System.out.println(maskbinario);
//                        System.out.println(ip1);
//                        System.out.println(ip2);
                        if(ip1.equals(ip2)){
                            for (int i = 0; i < 4; i++) {
                                System.out.println("Reply from "+ p.getDireccionIp() +": "+ "Ping exitoso");
                            }
                        }else{
                            for (int i = 0; i < 4; i++) {
                                System.out.println("Reply from "+ p.getDireccionIp()+": "+ " Destination host unreachable");
                            }
                        }
                    }
                    
                }
                if(verifier == 0){
                    for (int i = 0; i < 4; i++) {
                            System.out.println("Request timed out");
                        }
                }
            }
                
            
        
        }while(b !=true);
        PanelBase.setVisible(true);
        
    }//GEN-LAST:event_ButtonIngresarPcMouseClicked

